html:
{%- set company_doc = frappe.get_doc("Company", doc.company) -%}

<style>
	.sa-credit {
		font-family: Arial, Helvetica, sans-serif;
		font-size: 11pt;
		line-height: 1.5;
		color: #000;
	}
	.sa-credit .header {
		margin-bottom: 30px;
		border-bottom: 2px solid #c0392b;
		padding-bottom: 15px;
	}
	.sa-credit .company-logo {
		max-width: 200px;
		max-height: 80px;
		margin-bottom: 10px;
	}
	.sa-credit .company-name {
		font-size: 18pt;
		font-weight: bold;
		color: #c0392b;
		margin-bottom: 5px;
	}
	.sa-credit .credit-title {
		font-size: 24pt;
		font-weight: bold;
		text-align: center;
		color: #c0392b;
		margin: 20px 0;
		text-transform: uppercase;
	}
	.sa-credit .notice-box {
		background-color: #fadbd8;
		border-left: 4px solid #c0392b;
		padding: 15px;
		margin: 20px 0;
		font-weight: bold;
	}
	.sa-credit .address-section {
		display: table;
		width: 100%;
		margin-bottom: 20px;
	}
	.sa-credit .bill-to, .sa-credit .credit-details {
		display: table-cell;
		vertical-align: top;
		width: 50%;
	}
	.sa-credit .bill-to {
		padding-right: 20px;
	}
	.sa-credit .section-label {
		font-weight: bold;
		font-size: 10pt;
		color: #74808b;
		text-transform: uppercase;
		margin-bottom: 8px;
	}
	.sa-credit .vat-number {
		font-weight: bold;
		color: #c0392b;
	}
	.sa-credit table.items {
		width: 100%;
		border-collapse: collapse;
		margin: 20px 0;
	}
	.sa-credit table.items th {
		background-color: #c0392b;
		color: white;
		padding: 10px;
		text-align: left;
		font-weight: bold;
		border: 1px solid #a93226;
	}
	.sa-credit table.items td {
		padding: 8px 10px;
		border: 1px solid #ddd;
	}
	.sa-credit table.items tr:nth-child(even) {
		background-color: #f9f9f9;
	}
	.sa-credit .text-right {
		text-align: right;
	}
	.sa-credit .text-center {
		text-align: center;
	}
	.sa-credit .totals-section {
		float: right;
		width: 40%;
		margin-top: 20px;
	}
	.sa-credit .totals-table {
		width: 100%;
		border-collapse: collapse;
	}
	.sa-credit .totals-table td {
		padding: 8px;
		border-bottom: 1px solid #ddd;
	}
	.sa-credit .totals-table .label {
		text-align: right;
		font-weight: bold;
	}
	.sa-credit .totals-table .amount {
		text-align: right;
		width: 40%;
	}
	.sa-credit .totals-table .grand-total {
		font-size: 14pt;
		font-weight: bold;
		background-color: #c0392b;
		color: white;
	}
	.sa-credit .footer {
		clear: both;
		margin-top: 40px;
		padding-top: 20px;
		border-top: 2px solid #c0392b;
	}
	@media print {
		.sa-credit {
			font-size: 10pt;
		}
	}
</style>

<div class="sa-credit">
	<!-- Header Section -->
	<div class="header">
		<table style="width: 100%; border: none;">
			<tr>
				<td style="width: 50%; vertical-align: top; border: none;">
					{% if company_doc.company_logo %}
					<img src="{{ company_doc.company_logo }}" class="company-logo" alt="{{ company_doc.name }}">
					{% endif %}
					<div class="company-name">{{ company_doc.name }}</div>
					<div>{{ company_doc.address_line1 or "" }}</div>
					{% if company_doc.address_line2 %}
					<div>{{ company_doc.address_line2 }}</div>
					{% endif %}
					<div>{{ company_doc.city or "" }}{% if company_doc.state %}, {{ company_doc.state }}{% endif %} {{ company_doc.pincode or "" }}</div>
				</td>
				<td style="width: 50%; vertical-align: top; text-align: right; border: none;">
					<div class="section-label">Supplier VAT Number</div>
					<div class="vat-number">{{ company_doc.tax_id or "Not Registered" }}</div>
				</td>
			</tr>
		</table>
	</div>

	<!-- Credit Note Title -->
	<div class="credit-title">CREDIT NOTE</div>

	<!-- Notice Box -->
	<div class="notice-box">
		This Credit Note reduces the amount owed by the customer
	</div>

	<!-- Customer and Credit Details -->
	<div class="address-section">
		<div class="bill-to">
			<div class="section-label">Customer:</div>
			<div><strong>{{ doc.customer_name }}</strong></div>
			{% if doc.address_display %}
				{{ doc.address_display | replace("\n", "<br>") | safe }}
			{% endif %}
			{% set customer_doc = frappe.get_doc("Customer", doc.customer) if doc.customer else None %}
				{% if customer_doc and customer_doc.tax_id %}
			<div style="margin-top: 8px;">
				<span class="section-label">Customer VAT Number:</span><br>
				<span class="vat-number">{{ customer_doc.tax_id }}</span>
			</div>
			{% endif %}
		</div>
		<div class="credit-details">
			<table style="width: 100%; border: none;">
				<tr>
					<td style="border: none; padding: 4px;"><div class="section-label">Credit Note Number:</div></td>
					<td style="border: none; padding: 4px;"><strong>{{ doc.name }}</strong></td>
				</tr>
				<tr>
					<td style="border: none; padding: 4px;"><div class="section-label">Credit Note Date:</div></td>
					<td style="border: none; padding: 4px;">{{ doc.get_formatted("posting_date") }}</td>
				</tr>
				{% if doc.return_against %}
				<tr>
					<td style="border: none; padding: 4px;"><div class="section-label">Original Invoice:</div></td>
					<td style="border: none; padding: 4px;"><strong>{{ doc.return_against }}</strong></td>
				</tr>
				<tr>
					<td style="border: none; padding: 4px;"><div class="section-label">Invoice Date:</div></td>
					<td style="border: none; padding: 4px;">
						{% set original_invoice = frappe.get_doc("Sales Invoice", doc.return_against) %}
						{{ original_invoice.get_formatted("posting_date") }}
					</td>
				</tr>
				{% endif %}
			</table>
		</div>
	</div>

	<!-- Items Table -->
	<table class="items">
		<thead>
			<tr>
				<th style="width: 5%;" class="text-center">No.</th>
				<th style="width: 45%;">Description</th>
				<th style="width: 10%;" class="text-center">Qty</th>
				<th style="width: 15%;" class="text-right">Unit Price</th>
				<th style="width: 10%;" class="text-center">VAT %</th>
				<th style="width: 15%;" class="text-right">Amount</th>
			</tr>
		</thead>
		<tbody>
			{% for item in doc.items %}
			<tr>
				<td class="text-center">{{ loop.index }}</td>
				<td>
					<strong>{{ item.item_code }}</strong>
					{% if item.item_name and item.item_name != item.item_code %}
					<br>{{ item.item_name }}
					{% endif %}
					{% if item.description and item.description != item.item_name %}
					<br><span style="font-size: 9pt; color: #666;">{{ item.description }}</span>
					{% endif %}
				</td>
				<td class="text-center">{{ item.qty }} {{ item.uom or "" }}</td>
				<td class="text-right">{{ item.get_formatted("rate") }}</td>
				<td class="text-center">
					{% set item_tax_rate = 0 %}
					{% if doc.taxes %}
						{% for tax in doc.taxes %}
							{% if "VAT" in tax.description or "Tax" in tax.description %}
								{% set item_tax_rate = tax.rate %}
							{% endif %}
						{% endfor %}
					{% endif %}
					{{ "%.1f"|format(item_tax_rate) }}%
				</td>
				<td class="text-right">{{ item.get_formatted("amount") }}</td>
			</tr>
			{% endfor %}
		</tbody>
	</table>

	<!-- Totals Section -->
	<div class="totals-section">
		<table class="totals-table">
			<tr>
				<td class="label">Subtotal (Excl. VAT):</td>
				<td class="amount">{{ doc.get_formatted("net_total") }}</td>
			</tr>
			{% for tax in doc.taxes %}
			<tr>
				<td class="label">
					{{ tax.description }}
					{% if tax.rate %}@ {{ "%.1f"|format(tax.rate) }}%{% endif %}:
				</td>
				<td class="amount">{{ tax.get_formatted("tax_amount") }}</td>
			</tr>
			{% endfor %}
			<tr class="grand-total">
				<td class="label">CREDIT AMOUNT:</td>
				<td class="amount">{{ doc.get_formatted("grand_total") }}</td>
			</tr>
		</table>
	</div>

	<!-- Footer Section -->
	<div class="footer">
		{% if doc.remarks %}
		<div style="margin-bottom: 15px;">
			<div class="section-label">Reason for Credit:</div>
			<div>{{ doc.remarks }}</div>
		</div>
		{% endif %}

		<div style="margin-top: 20px; font-size: 9pt; color: #999; text-align: center;">
			This credit note complies with SARS requirements.<br>
			Reference to original invoice: {{ doc.return_against or "N/A" }}
		</div>
	</div>
</div>
