html:
{%- set company_doc = frappe.get_doc("Company", doc.company) -%}

<style>
	.sa-quotation {
		font-family: Arial, Helvetica, sans-serif;
		font-size: 11pt;
		line-height: 1.5;
		color: #000;
	}
	.sa-quotation .header {
		margin-bottom: 30px;
		border-bottom: 2px solid #2980b9;
		padding-bottom: 15px;
	}
	.sa-quotation .company-logo {
		max-width: 200px;
		max-height: 80px;
		margin-bottom: 10px;
	}
	.sa-quotation .company-name {
		font-size: 18pt;
		font-weight: bold;
		color: #2980b9;
		margin-bottom: 5px;
	}
	.sa-quotation .quotation-title {
		font-size: 24pt;
		font-weight: bold;
		text-align: center;
		color: #2980b9;
		margin: 20px 0;
		text-transform: uppercase;
	}
	.sa-quotation .address-section {
		display: table;
		width: 100%;
		margin-bottom: 20px;
	}
	.sa-quotation .quote-to, .sa-quotation .quotation-details {
		display: table-cell;
		vertical-align: top;
		width: 50%;
	}
	.sa-quotation .quote-to {
		padding-right: 20px;
	}
	.sa-quotation .section-label {
		font-weight: bold;
		font-size: 10pt;
		color: #74808b;
		text-transform: uppercase;
		margin-bottom: 8px;
	}
	.sa-quotation .vat-number {
		font-weight: bold;
		color: #2980b9;
	}
	.sa-quotation table.items {
		width: 100%;
		border-collapse: collapse;
		margin: 20px 0;
	}
	.sa-quotation table.items th {
		background-color: #2980b9;
		color: white;
		padding: 10px;
		text-align: left;
		font-weight: bold;
		border: 1px solid #2471a3;
	}
	.sa-quotation table.items td {
		padding: 8px 10px;
		border: 1px solid #ddd;
	}
	.sa-quotation table.items tr:nth-child(even) {
		background-color: #f9f9f9;
	}
	.sa-quotation .text-right {
		text-align: right;
	}
	.sa-quotation .text-center {
		text-align: center;
	}
	.sa-quotation .totals-section {
		float: right;
		width: 40%;
		margin-top: 20px;
	}
	.sa-quotation .totals-table {
		width: 100%;
		border-collapse: collapse;
	}
	.sa-quotation .totals-table td {
		padding: 8px;
		border-bottom: 1px solid #ddd;
	}
	.sa-quotation .totals-table .label {
		text-align: right;
		font-weight: bold;
	}
	.sa-quotation .totals-table .amount {
		text-align: right;
		width: 40%;
	}
	.sa-quotation .totals-table .grand-total {
		font-size: 14pt;
		font-weight: bold;
		background-color: #2980b9;
		color: white;
	}
	.sa-quotation .footer {
		clear: both;
		margin-top: 40px;
		padding-top: 20px;
		border-top: 2px solid #2980b9;
	}
	.sa-quotation .validity-notice {
		background-color: #eaf2f8;
		border-left: 4px solid #2980b9;
		padding: 15px;
		margin: 20px 0;
		font-weight: bold;
	}
	@media print {
		.sa-quotation {
			font-size: 10pt;
		}
	}
</style>

<div class="sa-quotation">
	<!-- Header Section -->
	<div class="header">
		<table style="width: 100%; border: none;">
			<tr>
				<td style="width: 50%; vertical-align: top; border: none;">
					{% if company_doc.company_logo %}
					<img src="{{ company_doc.company_logo }}" class="company-logo" alt="{{ company_doc.name }}">
					{% endif %}
					<div class="company-name">{{ company_doc.name }}</div>
					<div>{{ company_doc.address_line1 or "" }}</div>
					{% if company_doc.address_line2 %}
					<div>{{ company_doc.address_line2 }}</div>
					{% endif %}
					<div>{{ company_doc.city or "" }}{% if company_doc.state %}, {{ company_doc.state }}{% endif %} {{ company_doc.pincode or "" }}</div>
					<div>{{ company_doc.country or "" }}</div>
					{% if company_doc.phone %}
					<div>Phone: {{ company_doc.phone }}</div>
					{% endif %}
					{% if company_doc.email %}
					<div>Email: {{ company_doc.email }}</div>
					{% endif %}
				</td>
				<td style="width: 50%; vertical-align: top; text-align: right; border: none;">
					{% if company_doc.tax_id %}
					<div class="section-label">VAT Number</div>
					<div class="vat-number">{{ company_doc.tax_id }}</div>
					{% endif %}
					{% if company_doc.company_registration %}
					<div style="margin-top: 10px;">
						<strong>Reg No:</strong> {{ company_doc.company_registration }}
					</div>
					{% endif %}
				</td>
			</tr>
		</table>
	</div>

	<!-- Quotation Title -->
	<div class="quotation-title">QUOTATION</div>

	<!-- Quote To and Quotation Details -->
	<div class="address-section">
		<div class="quote-to">
			<div class="section-label">Quote To:</div>
			<div><strong>{{ doc.party_name or doc.customer_name }}</strong></div>
			{% if doc.address_display %}
				{{ doc.address_display | replace("\n", "<br>") | safe }}
			{% endif %}
			{% if doc.contact_display %}
			<div style="margin-top: 8px;">
				<strong>Contact:</strong> {{ doc.contact_display }}
			</div>
			{% endif %}
			{% if doc.contact_mobile %}
			<div><strong>Mobile:</strong> {{ doc.contact_mobile }}</div>
			{% endif %}
			{% if doc.contact_email %}
			<div><strong>Email:</strong> {{ doc.contact_email }}</div>
			{% endif %}
		</div>
		<div class="quotation-details">
			<table style="width: 100%; border: none;">
				<tr>
					<td style="border: none; padding: 4px;"><div class="section-label">Quotation Number:</div></td>
					<td style="border: none; padding: 4px;"><strong>{{ doc.name }}</strong></td>
				</tr>
				<tr>
					<td style="border: none; padding: 4px;"><div class="section-label">Quotation Date:</div></td>
					<td style="border: none; padding: 4px;">{{ doc.get_formatted("transaction_date") }}</td>
				</tr>
				<tr>
					<td style="border: none; padding: 4px;"><div class="section-label">Valid Until:</div></td>
					<td style="border: none; padding: 4px;">{{ doc.get_formatted("valid_till") if doc.valid_till else "N/A" }}</td>
				</tr>
			</table>
		</div>
	</div>

	<!-- Validity Notice -->
	{% if doc.valid_till %}
	<div class="validity-notice">
		This quotation is valid until {{ doc.get_formatted("valid_till") }}
	</div>
	{% endif %}

	<!-- Items Table -->
	<table class="items">
		<thead>
			<tr>
				<th style="width: 5%;" class="text-center">No.</th>
				<th style="width: 45%;">Description</th>
				<th style="width: 10%;" class="text-center">Qty</th>
				<th style="width: 15%;" class="text-right">Unit Price</th>
				<th style="width: 10%;" class="text-center">VAT %</th>
				<th style="width: 15%;" class="text-right">Amount</th>
			</tr>
		</thead>
		<tbody>
			{% for item in doc.items %}
			<tr>
				<td class="text-center">{{ loop.index }}</td>
				<td>
					<strong>{{ item.item_code }}</strong>
					{% if item.item_name and item.item_name != item.item_code %}
					<br>{{ item.item_name }}
					{% endif %}
					{% if item.description and item.description != item.item_name %}
					<br><span style="font-size: 9pt; color: #666;">{{ item.description }}</span>
					{% endif %}
				</td>
				<td class="text-center">{{ item.qty }} {{ item.uom or "" }}</td>
				<td class="text-right">{{ item.get_formatted("rate") }}</td>
				<td class="text-center">
					{% set item_tax_rate = 0 %}
					{% if doc.taxes %}
						{% for tax in doc.taxes %}
							{% if "VAT" in tax.description or "Tax" in tax.description %}
								{% set item_tax_rate = tax.rate %}
							{% endif %}
						{% endfor %}
					{% endif %}
					{{ "%.1f"|format(item_tax_rate) }}%
				</td>
				<td class="text-right">{{ item.get_formatted("amount") }}</td>
			</tr>
			{% endfor %}
		</tbody>
	</table>

	<!-- Totals Section -->
	<div class="totals-section">
		<table class="totals-table">
			<tr>
				<td class="label">Subtotal (Excl. VAT):</td>
				<td class="amount">{{ doc.get_formatted("net_total") }}</td>
			</tr>
			{% if doc.discount_amount and doc.discount_amount > 0 %}
			<tr>
				<td class="label">Discount:</td>
				<td class="amount">({{ doc.get_formatted("discount_amount") }})</td>
			</tr>
			{% endif %}
			{% for tax in doc.taxes %}
			<tr>
				<td class="label">
					{{ tax.description }}
					{% if tax.rate %}@ {{ "%.1f"|format(tax.rate) }}%{% endif %}:
				</td>
				<td class="amount">{{ tax.get_formatted("tax_amount") }}</td>
			</tr>
			{% endfor %}
			<tr class="grand-total">
				<td class="label">TOTAL (Incl. VAT):</td>
				<td class="amount">{{ doc.get_formatted("grand_total") }}</td>
			</tr>
		</table>
	</div>

	<!-- Footer Section -->
	<div class="footer">
		{% if doc.payment_terms_template %}
		<div style="margin-bottom: 15px;">
			<div class="section-label">Payment Terms:</div>
			<div>{{ doc.payment_terms_template }}</div>
		</div>
		{% endif %}

		{% if doc.terms %}
		<div style="margin-bottom: 15px; font-size: 10pt;">
			<div class="section-label">Terms and Conditions:</div>
			<div>{{ doc.terms }}</div>
		</div>
		{% endif %}

		<div style="margin-top: 30px; padding: 15px; background-color: #eaf2f8; border-radius: 5px;">
			<div style="font-weight: bold; margin-bottom: 8px;">To Accept This Quotation:</div>
			<div style="font-size: 10pt;">
				Please sign and return this quotation, or reply via email confirming your acceptance.
				A formal Tax Invoice will be issued upon order confirmation.
			</div>
		</div>

		<div style="margin-top: 30px;">
			<div style="border-top: 1px solid #000; width: 250px; margin-top: 50px;">
				<div style="margin-top: 5px; font-size: 10pt;">Authorized Signature</div>
			</div>
		</div>
	</div>
</div>
