html:
{%- set company_doc = frappe.get_doc("Company", doc.company) -%}
{%- include "za_local/print_format/sa_sales_invoice/sa_sales_invoice.html" with context -%}
{# Reuse Sales Invoice template but override title #}
<script>
document.addEventListener('DOMContentLoaded', function() {
	var titleEl = document.querySelector('.sa-invoice .invoice-title');
	if (titleEl) {
		titleEl.textContent = 'SALES ORDER';
		titleEl.style.color = '#27ae60';
	}
	var headerBorder = document.querySelector('.sa-invoice .header');
	if (headerBorder) {
		headerBorder.style.borderBottomColor = '#27ae60';
	}
	var tableThs = document.querySelectorAll('.sa-invoice table.items th');
	tableThs.forEach(function(th) {
		th.style.backgroundColor = '#27ae60';
		th.style.borderColor = '#229954';
	});
	var grandTotal = document.querySelector('.sa-invoice .grand-total');
	if (grandTotal) {
		grandTotal.style.backgroundColor = '#27ae60';
	}
});
</script>
